

function ico(file,target_h,cb){
    createReader(file,function(w,h){

        var qlt = target_h/h;
        var data = '';
        imgReady(file,function(data){
            lrz(data,{
                width:w*qlt,
                height:target_h
            }).then(function(rst){
                cb(rst.base64);
            });
        });

    });


    function createReader(file, whenReady) {
        var reader = new FileReader;
        reader.onload = function (evt) {
            var image = new Image();
            image.onload = function () {
                var width = this.width;
                var height = this.height;
                if (whenReady) whenReady(width, height);
            };
            image.src = evt.target.result;
        };
        reader.readAsDataURL(file);
    }

    function imgReady(obj,cb){
        var picFile = obj;
        var b64 = '';
        var reader = new FileReader();
        reader.readAsDataURL(picFile);

        reader.onload = function(f){
            var res = this.result;
            var img = new Image();
            
            img.onload = function(){
                b64 = compress(img, picFile.type);
                cb(b64);
                img = null;
            };
            img.src = res;
        }
    }


    function rotateImg(img, direction,canvas,fileType) {
        var min_step = 0;
        var max_step = 3;
        //var img = document.getElementById(pid);
        if (img == null)return;
        //img的高度和宽度不能在img元素隐藏后获取，否则会出错
        var height = img.height;
        var width = img.width;
        //var step = img.getAttribute('step');
        var step = 2;
        if (step == null) {
            step = min_step;
        }
        if (direction == 'right') {
            step++;
            //旋转到原位置，即超过最大值
            step > max_step && (step = min_step);
        } else {
            step--;
            step < min_step && (step = max_step);
        }
        var degree = step * 90 * Math.PI / 180;
        var ctx = canvas.getContext('2d');
        switch (step) {
            case 0:
                canvas.width = width;
                canvas.height = height;
                ctx.drawImage(img, 0, 0);
                break;
            case 1:
                canvas.width = height;
                canvas.height = width;
                ctx.rotate(degree);
                ctx.drawImage(img, 0, -height);
                break;
            case 2:
                canvas.width = width;
                canvas.height = height;
                ctx.rotate(degree);
                ctx.drawImage(img, -width, -height);
                break;
            case 3:
                canvas.width = height;
                canvas.height = width;
                ctx.rotate(degree);
                ctx.drawImage(img, -width, 0);
                break;
        }
        return canvas.toDataURL(fileType,0.75);
    }

    function compress(img, fileType) {
        var canvas = document.createElement("canvas");
        var rotateshow;
        EXIF.getData(img, function(){
            EXIF.getAllTags(img);
            Orientation = EXIF.getTag(img,'Orientation');
            switch (Orientation){
                case 6:
                    rotateshow = rotateImg(img,'left',canvas,fileType);
                    break;
                case 8:
                    rotateshow = rotateImg(img,'right',canvas,fileType);
                    break;
                case 3:
                    rotateImg(img,'right',canvas,fileType);
                    rotateshow = rotateImg(img,'right',canvas,fileType);
                    break;
                default:
                    rotateshow = img.src;
            }

        });
        return rotateshow;
    }

    function blobToFile(theBlob, fileName){
        //A Blob() is almost a File() - it's just missing the two properties below which we will add
        theBlob.lastModifiedDate = new Date();
        theBlob.name = fileName;
        return theBlob;
    }
}








